let inputDirection={x:0,y:0},lastInputDirection={x:0,y:0};window.addEventListener("keydown",e=>{switch(e.key){case"ArrowUp":if(0!==lastInputDirection.y)break;inputDirection={x:0,y:-1};break;case"ArrowDown":if(0!==lastInputDirection.y)break;inputDirection={x:0,y:1};break;case"ArrowLeft":if(0!==lastInputDirection.x)break;inputDirection={x:-1,y:0};break;case"ArrowRight":if(0!==lastInputDirection.x)break;inputDirection={x:1,y:0}}});export const getInputDirection=()=>(lastInputDirection=inputDirection,inputDirection);